package Package1;

public class IntSum {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int num1 = 20;
		int num2 = 30;
		int sum = num1 + num2;
		
		System.out.println("Sum of two number is :" + sum );
		
		String name = "Clean India Green India";
		System.out.println("Total number of Chars are " + name.length());
		
		char char1= name.charAt(0); 
		System.out.println("Last Char is " + name.charAt(name.length()-1));
		System.out.println("Char at 1st place is " + char1 );
		System.out.println("The String UpperCase is " + name.toUpperCase());
		System.out.println("The String LowerCase is " + name.toLowerCase());
		
	}

	
	
}
